using CommunityToolkit.Mvvm.ComponentModel;using Flurl.Http;using Flurl.Util;namespace CurrencyConverter;public partial class MainViewModel : ObservableObject{    public MainViewModel()    {        var url = "https://www.cbr-xml-daily.ru/daily_json.js";        var result = url.GetJsonAsync<Root>().Result;        findableDateTime = result.Date;        rateValue = result.Valute.USD.Value;        foreach(var valutes in result.Valute.ListOfValuteItem)        {            ListOfValutes.Add(valutes.Name);        }            }    [ObservableProperty]    DateTime userDateTime;    [ObservableProperty]    DateTime findableDateTime;    [ObservableProperty]    [NotifyPropertyChangedFor(nameof(convertedCurrencyValue))]    double userCurrencyValue = 1;    [ObservableProperty]    double rateValue;    [ObservableProperty]    List<string> listOfValutes = new List<string>();    [ObservableProperty]    Valute selectedValute;    public double convertedCurrencyValue => userCurrencyValue * rateValue;}